PREFIX sh: <http://www.w3.org/ns/shacl#> .
CONSTRUCT {
?s sh:nodeKind sh:blankNodeOrIRI
} WHERE {
?s a sh:propertyShape ;
(sh:class) ?o .
FILTER( NOT EXISTS { ?s sh:nodeKind ?o2 } )
}
PREFIX sh: <http://www.w3.org/ns/shacl#> .
CONSTRUCT {
?s sh:nodeKind sh:Literal
} WHERE {
?s a sh:propertyShape ;
(sh:datatype|sh:languageIn|sh:minExclusive|sh:maxExclusive|sh:minInclusive|sh:maxInclusive|sh:minLength|sh:maxLength|sh:pattern|sh:flags|sh:uniqueLang|sh:lessThan|sh:lessThanOrEquals) ?o .
FILTER( NOT EXISTS { ?s sh:nodeKind ?o2 } )
}
